import { HttpClient,HttpHeaders  } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Task } from '../../model/task';
import { Observable } from 'rxjs';


@Injectable({
  providedIn: 'root'
})
export class CrudService {
  
  createAuthorizationHeader(headers: Headers) {
    headers.append('token','B'+this.token); 
  }
  serviceURL:string;  
  constructor(private http:HttpClient) { 
    this.serviceURL='https://643fd869b9e6d064be006110.mockapi.io/tasks/task'

  }

 async addTasks(task:Task):Observable<Task>{
     let headers = new Headers();
     let token = await localStorage.getItem('token');
    this.createAuthorizationHeader(headers);
    return this.http.post<Task>('http://localhost:5000/task/addTask',task,{
      headers: new HttpHeaders().set('Authorization','b'+this.token)
  })
  }
  getAllTask():Observable<Task[]>{
    return this.http.get<Task[]>(this.serviceURL)
  }
  deleteTask(task:Task):Observable<Task>{
    return this.http.delete<Task>(this.serviceURL+'/'+task.id)
  }
  editTask(task:Task):Observable<Task>{
    return this.http.put<Task>(this.serviceURL+'/'+task.id,task)
  }
}
