        // this.router.navigate(['/home']);
        // this.isLoggedIn()
        return this.http.post(`${this.serviceURL}signin`,data)
        // });
    }
    
    isLoggedIn(): boolean {
        let email = localStorage.getItem('email');
        let password=localStorage.getItem('password');
        return email && password ? true:false
      }
  }
